import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [leetcodeUrl, setLeetcodeUrl] = useState('');
  const [currentProblem, setCurrentProblem] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to the bottom of the chat
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);



  // Add this function to your App.js component
const renderFormattedText = (text) => {
  // This simple renderer handles basic markdown-like formatting
  // For more complex formatting, consider using a library like marked or react-markdown

  // Handle paragraphs
  const paragraphs = text.split('\n\n');

  return (
    <>
      {paragraphs.map((paragraph, index) => {
        // Skip empty paragraphs
        if (!paragraph.trim()) return null;

        // Handle bold text
        let formattedParagraph = paragraph.replace(
          /\*\*(.*?)\*\*/g, 
          '<strong>$1</strong>'
        );

        // Handle italic text
        formattedParagraph = formattedParagraph.replace(
          /\*(.*?)\*/g, 
          '<em>$1</em>'
        );

        // Handle code blocks
        formattedParagraph = formattedParagraph.replace(
          /`([^`]+)`/g,
          '<code>$1</code>'
        );

        return (
          <p 
            key={index} 
            dangerouslySetInnerHTML={{ __html: formattedParagraph }}
          />
        );
      })}
    </>
  );
};

// Then update your message rendering in the JSX part:
// Replace this line:
// <div className="message-text">{message.text}</div>

// With this:
<div className="message-text">
  {message.sender === 'assistant' 
    ? renderFormattedText(message.text)
    : message.text
  }
</div>


  // Format timestamps
  const formatTime = () => {
    const now = new Date();
    return now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Check if input is a LeetCode URL
  const isLeetCodeUrl = (text) => {
    return /leetcode\.com\/problems\/[\w-]+/.test(text);
  };

  // Extract problem name from URL
  const extractProblemName = (url) => {
    const match = url.match(/problems\/([\w-]+)/);
    if (match && match[1]) {
      return match[1].split('-').map(word => 
        word.charAt(0).toUpperCase() + word.slice(1)
      ).join(' ');
    }
    return 'Unknown Problem';
  };

  // Handle sending a message
  const handleSend = async (e) => {
    e.preventDefault();
    if (!inputValue.trim()) return;

    const inputText = inputValue.trim();
    let messageText = inputText;
    let systemMessage = null;

    // Check if the input is a LeetCode URL
    if (isLeetCodeUrl(inputText) && !currentProblem) {
      const problemName = extractProblemName(inputText);
      setCurrentProblem({
        url: inputText,
        name: problemName
      });
      setLeetcodeUrl(inputText);

      // Add system message about the problem selection
      systemMessage = {
        id: Date.now(),
        sender: 'system',
        text: `Problem selected: ${problemName}`,
        time: formatTime(),
      };

      messageText = `I'd like help with this LeetCode problem: ${inputText}`;
    }

    // Add user message to the chat
    const userMessage = {
      id: Date.now() + 1,
      sender: 'user',
      text: messageText,
      time: formatTime(),
    };

    // Update messages array
    const updatedMessages = [
      ...messages, 
      ...(systemMessage ? [systemMessage] : []), 
      userMessage
    ];

    setMessages(updatedMessages);
    setInputValue('');
    setIsLoading(true);

    // Prepare conversation for API
    const conversation = updatedMessages.map((msg) => ({
      role: msg.sender === 'user' ? 'user' : 
            msg.sender === 'system' ? 'system' : 'assistant',
      content: msg.text,
    }));

    try {
      const response = await fetch('http://localhost:8000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ messages: conversation }),
      });

      const data = await response.json();

      // Add AI response to chat
      const aiText = data.choices[0].message.content;
      const aiMessage = {
        id: Date.now() + 2,
        sender: 'assistant',
        text: aiText,
        time: formatTime(),
      };

      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error('Error:', err);
      setMessages((prev) => [
        ...prev,
        {
          id: Date.now() + 2,
          sender: 'system',
          text: 'Sorry, there was an error connecting to the AI. Please try again.',
          time: formatTime(),
        },
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle URL input change
  const handleUrlChange = (e) => {
    setLeetcodeUrl(e.target.value);
  };

  // Submit LeetCode URL
  const submitUrl = (e) => {
    e.preventDefault();
    if (!leetcodeUrl.trim() || !isLeetCodeUrl(leetcodeUrl)) {
      setMessages((prev) => [
        ...prev,
        {
          id: Date.now(),
          sender: 'system',
          text: 'Please enter a valid LeetCode problem URL (e.g., https://leetcode.com/problems/two-sum/)',
          time: formatTime(),
        },
      ]);
      return;
    }

    const problemName = extractProblemName(leetcodeUrl);
    setCurrentProblem({
      url: leetcodeUrl,
      name: problemName
    });

    setMessages((prev) => [
      ...prev,
      {
        id: Date.now(),
        sender: 'system',
        text: `Problem selected: ${problemName}`,
        time: formatTime(),
      },
    ]);

    setLeetcodeUrl('');
    // Focus on the question input after URL submission
    document.getElementById('questionInput').focus();
  };

  // Clear current problem
  const handleClearProblem = () => {
    setCurrentProblem(null);
    setMessages((prev) => [
      ...prev,
      {
        id: Date.now(),
        sender: 'system',
        text: 'Problem cleared. You can select a new problem.',
        time: formatTime(),
      },
    ]);
  };

  // Rendering
  return (
    <div className="app-container">
      <header className="app-header">
        <h1>LeetCode Assistant</h1>
        <p>Ask questions about LeetCode problems and get guidance</p>
      </header>

      <main className="main-container">
        <div className="chat-container">
          {/* Problem selection area */}
          <div className="problem-selection">
            {currentProblem ? (
              <div className="current-problem">
                <div className="problem-info">
                  <h3>{currentProblem.name}</h3>
                  <a href={currentProblem.url} target="_blank" rel="noopener noreferrer">
                    View on LeetCode
                  </a>
                </div>
                <button 
                  className="clear-problem-btn"
                  onClick={handleClearProblem}
                >
                  Change Problem
                </button>
              </div>
            ) : (
              <form onSubmit={submitUrl} className="url-form">
                <label htmlFor="leetcodeUrl">Enter LeetCode Problem URL:</label>
                <div className="url-input-container">
                  <input
                    type="text"
                    id="leetcodeUrl"
                    placeholder="https://leetcode.com/problems/two-sum/"
                    value={leetcodeUrl}
                    onChange={handleUrlChange}
                  />
                  <button type="submit">Set Problem</button>
                </div>
              </form>
            )}
          </div>

          {/* Chat messages area */}
          <div className="chat-messages">
            {messages.length === 0 ? (
              <div className="welcome-message">
                <h3>Welcome to LeetCode Assistant!</h3>
                <p>Submit a LeetCode problem URL to get started, then ask your questions about the problem.</p>
                <div className="example-list">
                  <p><strong>Examples of what you can ask:</strong></p>
                  <ul>
                    <li>"Can you explain the problem statement in simpler terms?"</li>
                    <li>"What's a good approach to solve this problem?"</li>
                    <li>"I'm stuck with time complexity issues. How can I optimize my solution?"</li>
                    <li>"Can you help me find the bug in my approach?"</li>
                    <li>"What edge cases should I consider?"</li>
                  </ul>
                </div>
              </div>
            ) : (
              messages.map((message) => (
                <div 
                  key={message.id} 
                  className={`message-wrapper ${message.sender}`}
                >
                  <div className={`message ${message.sender}`}>
                    {message.sender === 'assistant' && (
                      <div className="message-avatar">üë®‚Äçüíª</div>
                    )}
                    {message.sender === 'user' && (
                      <div className="message-avatar">üë§</div>
                    )}
                    <div className="message-content">
                      <div className="message-text">
                        {message.text}
                      </div>
                      <div className="message-time">{message.time}</div>
                    </div>
                  </div>
                </div>
              ))
            )}

            {isLoading && (
              <div className="message-wrapper assistant">
                <div className="message assistant">
                  <div className="message-avatar">üë®‚Äçüíª</div>
                  <div className="message-content">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Message input area */}
          <form onSubmit={handleSend} className="chat-form">
            <input
              id="questionInput"
              type="text"
              placeholder={currentProblem 
                ? `Ask a question about ${currentProblem.name}...` 
                : "Enter a LeetCode URL or ask a general coding question..."
              }
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              disabled={isLoading}
            />
            <button 
              type="submit" 
              disabled={!inputValue.trim() || isLoading}
            >
              {isLoading ? 'Sending...' : 'Send'}
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}

export default App;
