import React, { useState, useEffect, useRef } from 'react';
import './index.css';

const dummyUsers = [
  { id: 1, name: 'Alice', avatar: 'ðŸ‘©â€ðŸ’¼' },
  { id: 2, name: 'Bob', avatar: 'ðŸ‘¨â€ðŸ’»' },
  { id: 3, name: 'Charlie', avatar: 'ðŸ§‘â€ðŸŽ¨' },
  { id: 4, name: 'Diana', avatar: 'ðŸ‘©â€ðŸ”¬' },
  { id: 5, name: 'Ethan', avatar: 'ðŸ‘¨â€ðŸš€' },
];

function Explanation() {
  const [showCode, setShowCode] = useState(false);

  const toggleCode = () => {
    setShowCode(!showCode);
  };

  return (
    <div className="explanation-container">
      <h1>LeetCode: Number of Sub-arrays with Odd Sum</h1>
      <section>
        <h2>Problem Statement</h2>
        <p>
          Given an integer array, find the number of subarrays whose sum is odd.
          A subarray is a contiguous section of the array.
        </p>
      </section>
      <section>
        <h2>Approach</h2>
        <ol>
          <li>
            <strong>Prefix Sums:</strong> Calculate the cumulative sum from the start of the array.
          </li>
          <li>
            <strong>Counting Parity:</strong> Maintain counts of even and odd prefix sums.
          </li>
          <li>
            <strong>Logic:</strong> For subarray <code>arr[i:j]</code>, the sum is <code>prefix[j] - prefix[i-1]</code>. This is odd if one prefix is even and the other is odd.
          </li>
          <li>
            <strong>Counting:</strong> For each prefix, add the count of previously seen prefixes with opposite parity.
          </li>
        </ol>
      </section>
      <section>
        <h2>Python Code</h2>
        <button className="toggle-btn" onClick={toggleCode}>
          {showCode ? 'Hide Code' : 'Show Code'}
        </button>
        {showCode && (
          <pre className="code-block">
{`class Solution:
    def numOfSubarrays(self, arr: List[int]) -> int:
        MOD = 10**9 + 7
        count = 0
        odd_prefix = 0
        even_prefix = 1  # Empty prefix sum is even
        current_sum = 0

        for num in arr:
            current_sum += num
            if current_sum % 2 == 0:
                count = (count + odd_prefix) % MOD
                even_prefix += 1
            else:
                count = (count + even_prefix) % MOD
                odd_prefix += 1

        return count`}
          </pre>
        )}
      </section>
      <section>
        <h2>Key Improvements</h2>
        <ul>
          <li>
            <strong>Modulo Arithmetic:</strong> Prevents integer overflow.
          </li>
          <li>
            <strong>Efficiency:</strong> The prefix sum approach runs in O(n) time.
          </li>
          <li>
            <strong>Initialization:</strong> <code>even_prefix</code> starts at 1 to account for the empty prefix.
          </li>
          <li>
            <strong>Clear Variables:</strong> Descriptive names improve readability.
          </li>
        </ul>
      </section>
    </div>
  );
}

function ChatApp() {
  const [selectedUser, setSelectedUser] = useState(dummyUsers[0]);
  const [messages, setMessages] = useState([]);
  const [textInput, setTextInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const handleUserSelect = (user) => {
    setSelectedUser(user);
    setMessages([]);
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const formatTime = () => {
    const now = new Date();
    return now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!textInput.trim()) return;

    const userMessage = {
      id: Date.now(),
      sender: 'user',
      text: textInput,
      time: formatTime(),
    };

    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setTextInput('');
    setIsLoading(true);

    const conversation = updatedMessages.map((msg) => ({
      role: msg.sender === 'user' ? 'user' : 'assistant',
      content: msg.text,
    }));

    try {
      const response = await fetch('http://localhost:8000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ messages: conversation }),
      });
      const data = await response.json();
      const aiText = data.choices[0].message.content;
      const aiMessage = {
        id: Date.now() + 1,
        sender: 'assistant',
        text: aiText,
        time: formatTime(),
      };

      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error('Error:', err);
      setMessages((prev) => [
        ...prev,
        {
          id: Date.now() + 1,
          sender: 'system',
          text: 'Sorry, there was an error connecting to the AI. Please try again.',
          time: formatTime(),
        },
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-app">
      <div className="chat-header">
        <h2>Chat with {selectedUser.name}</h2>
      </div>
      <div className="chat-window">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <p>No messages yet. Start a conversation!</p>
          </div>
        ) : (
          messages.map((message) => (
            <div key={message.id} className={`message-wrapper ${message.sender}`}>
              <div className={`message ${message.sender}`}>
                {message.sender === 'assistant' && (
                  <div className="message-avatar">ðŸ¤–</div>
                )}
                {message.sender === 'user' && (
                  <div className="message-avatar">{selectedUser.avatar}</div>
                )}
                <div className="message-content">
                  <div className="message-text">{message.text}</div>
                  <div className="message-time">{message.time}</div>
                </div>
              </div>
            </div>
          ))
        )}
        {isLoading && (
          <div className="message-wrapper assistant">
            <div className="message assistant">
              <div className="message-avatar">ðŸ¤–</div>
              <div className="message-content">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={handleSend} className="chat-form">
        <input
          type="text"
          placeholder={`Message ${selectedUser.name}...`}
          value={textInput}
          onChange={(e) => setTextInput(e.target.value)}
          disabled={isLoading}
        />
        <button
          type="submit"
          disabled={!textInput.trim() || isLoading}
          className={isLoading ? 'loading' : ''}
        >
          {isLoading ? '...' : 'Send'}
        </button>
      </form>
    </div>
  );
}

function App() {
  const [showExplanation, setShowExplanation] = useState(false);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>AI Chat and Explanation App</h1>
        <div className="header-buttons">
          <button onClick={() => setShowExplanation(false)}>Chat</button>
          <button onClick={() => setShowExplanation(true)}>Explanation</button>
        </div>
      </header>
      <div className="main-container">
        {showExplanation ? <Explanation /> : <ChatApp />}
      </div>
    </div>
  );
}

export default App;
